function createLGradient(t=0,n=0,e=999,r=999,o={}){o={0:"#000",1:"#fff",...o};const a=drawingContext.createLinearGradient(t,n,e,r);return Object.entries(o).forEach(t=>{a.addColorStop(t[0],t[1])}),a}function createRGradient(t=0,n=0,e=0,r=200,o=200,a=200,d={}){d={0:"#000",1:"#fff",...d};const i=drawingContext.createRadialGradient(t,n,e,r,o,a);return Object.entries(d).forEach(t=>{i.addColorStop(t[0],t[1])}),i}function shadow(t=0,n=0,e=10,r="#000"){drawingContext.shadowColor=r,drawingContext.shadowOffsetX=t,drawingContext.shadowOffsetY=n,drawingContext.shadowBlur=e}function snap(t,n){return Math.floor(t/n)*n}